<?phpnamespace Sistema\Util;class SysFnc {		public static function FechaActualYmdHms()	{		$time = time();        $fechaCompleta=date("Y-m-d H:i:s", $time);		return $fechaCompleta;	}    public static function FechaActualYmd()	{		$time = time();        $fechaCompleta=date("Y-m-d", $time);		return $fechaCompleta;	}        public static function rutFormat($rut,$dv)    {        if($rut!="" && $dv!=""){        return number_format($rut,-3,"",".")."-".$dv;      }else{        return "";      }     }    public static function mostrarFecha($fecha)    {       setlocale(LC_TIME, 'spanish').': ';       $valor = ucfirst(iconv('ISO-8859-1', 'UTF-8', strftime('%A %d de %B del %Y', strtotime($fecha))))."";             return $valor;    }        public static function difDays($fechaInicio)    {         $time = time();         $diferencia = strtotime(date("Y-m-d H:i:s", $time)) - strtotime($fechaInicio);         $dia = floor($diferencia/86400);         return $dia;    }        public static function correoValido($correo)    {        if($correo!=""){        $Sintaxis='#^[\w.-]+@[\w.-]+\.[a-zA-Z]{2,6}$#';            if(preg_match($Sintaxis,$correo))                return true;            else            return false;        }else{            return true;        }    }            public static function rspOK()    {        return ' <div class="modal fade" id="respuestaOK" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true" data-backdrop="static">               <div class="modal-dialog">                 <div class="modal-content" >                         <div >                         &nbsp;                            <div class="container-fluid">                                <div class="row">                                    <div class="col-lg-12">                                        <div class="col-lg-12 text-center" style="padding: 80px;">                                            <span><i class="fa fa-thumbs-o-up fa-5x"></i></span>                                            <h2 id="descrok"></h2>                                            </div>                                    </div>                                </div>                            </div>                            </div>                    </div>                </div>            </div>  ';    }        public static function nuevaPersona()    {        return '<div class="modal fade" id="modalRegistrarPersona" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true" data-backdrop="static">               <div class="modal-dialog">                 <div class="modal-content" >                                                 <div >                         &nbsp;                            <div class="container-fluid">                                <div class="row">                                    <div class="col-lg-12">                                        <div class="panel panel-info">                                            <div class="panel-heading">                                            Registrar Nuevo                                         </div>                                        <div class="panel-body">                                            <div id="alertaP1"></div>                                            <div class="form-group input-group">                                               <span class="input-group-addon"><i class="fa fa-user"></i> Tipo de Persona</span>                                               <select class="form-control" id="tipoP1">                                                    <option value="natural">Natural</option>                                                    <option value="juridica">Juridica</option>                                               </select>                                            </div>                                            <div id="mostrarDatosP1">                                            <div class="form-group input-group">                                               <span class="input-group-addon">Rut</span>                                               <input type="text" class="form-control" id="rutP1"/>                                                                                           </div>                                            <div id="mostrarPersonaNatual">                                                <div class="form-group input-group">                                                   <span class="input-group-addon">Dni</span>                                                   <input type="text" class="form-control" id="dniP1"/>                                                </div>                                                <div class="form-group input-group">                                                   <span class="input-group-addon">Nombres</span>                                                   <input type="text" class="form-control" id="nombreP1"/>                                                </div>                                                <div class="form-group input-group">                                                    <span class="input-group-addon">Apellido P</span>                                                    <input type="text" id="apellidoP1" class="form-control"/>                                                </div>                                                <div class="form-group input-group">                                                       <span class="input-group-addon">Apellido M</span>                                                    <input type="text" id="apellido2P1" class="form-control"/>                                                </div>                                            </div>                                            <div id="mostrarPersonaJuridica">                 