P1").hide();                $("input#rutP1").rut({validateOn: "change"}).on("rutInvalido", function(){$("#alertaP1").html("<font color=red>Error: Rut Invalido</font>");$("#btnRegistrarNuevaPersona").attr("disabled","disabled");});                $("input#rutP1").rut({validateOn: "change"}).on("rutValido", function(){$("#alertaP1").html("");$("#btnRegistrarNuevaPersona").removeAttr("disabled");})                                 $("#rutP1").change(function(){                    if($(this).val()==""){                        $("#alertaP1").html("");                        $("#btnRegistrarNuevaPersona").removeAttr("disabled");                    }                });                                                $("#tipoP1").change(function(){                    $("#alertaP1").html("");                    $("#rutP1").val("");$("#dniP1").val("");$("#nombreP1").val("");$("#apellidoP1").val("");                    $("#apellido2P1").val("");$("#nombreRazonP1").val("");$("#telefonoP1").val("");$("#correoP1").val("");                    if($(this).val()==""){                        $("#mostrarDatosP1").hide(500);                        }else{                                                        if($(this).val()=="natural"){                                $("#mostrarPersonaJuridica").hide();                                $("#mostrarPersonaNatual").show();                            }else{                               $("#mostrarPersonaJuridica").show();                               $("#mostrarPersonaNatual").hide();                            }                            $("#mostrarDatosP1").show(700);                                                                            }                                    });                $("#tipoP1").trigger("change");              function registrarPersonaNueva(){                                    var tipop1 = $("#tipoP1").val();                    var rutp1 =$("#rutP1").val();                    var dnip1 =$("#dniP1").val();                    var nombrep1 =$("#nombreP1").val();                    var apellidop1 =$("#apellidoP1").val();                    var apellido2p1 =$("#apellido2P1").val();                    var telefonop1 =$("#telefonoP1").val();                    var correop1 =$("#correoP1").val();                                                            if(tipop1=="")                    {                        $("#alertaP1").html("<font color=red>Error: No ha seleccionado tipo de persona</font>");                        return;                    }                    if(tipop1=="juridica")                    {                                              nombrep1 = $("#nombreRazonP1").val();                        if (rutp1==""){                            $("#alertaP1").html("<font color=red>Error: Rut es obligatorio para persona Juridica</font>");                        return;                        }                    }                    var vector = new Array();                    var parametros = {"tipop1":tipop1,"rutp1":rutp1,"dnip1":dnip1,"nombrep1":nombrep1,"apellidop1":apellidop1,"apellido2p1":apellido2p1,"telefonop1":telefonop1,"correop1":correop1};                                      	$.ajax( {             	        async:false,                            cache:false,                 		data : parametros,                        url : "'.$_SERVER['REDIRECT_BASE'].'conserje/cfg/regper",                		type : "post",                		success : function(r) {                            if(r.status=="ok"){                                                                $("#rutP1").val("");                                $("#dniP1").val("");                                $("#nombreP1").val("");                                $("#apellidoP1").val("");                                $("#apellido2P1").val("");                                $("#telefonoP1").val("");                                $("#correoP1").val("");                                vector["id"] = r.idpersona;                                vector["rut"] = rutp1;                                vector["dv"] = r.dv;                                vector["dni"] = dnip1;                                vector["nombre"] = nombrep1;                                vector["apellido1"] = apellidop1;                                vector["apellido2"] = apellido2p1;                                vector["telefono"] = telefonop1;                                vector["correo"] = correop1;                                vector["rutformat"] = r.rutformat;                                                                $("#descrok").text("Se ha registrado la persona satisfactoriamente...");                                $("#respuestaOK").modal("show");                                setTimeout(function(){$("#respuestaOK").modal("hide");},1500);                                                                $("#modalRegistrarPersona").modal("hide");                                                            }else{                                $("#alertaP1").html("<font color=red>"+r.error+"</font>");                            }                		}                    });                                                                              return vector;                                    }                function limpiarPersona(){                                        $("#alertaP1").html("");                    $("#tipoP1").val("");                    $("#rutP1").val("");                    $("#dniP1").val("");                    $("#nombreP1").val("");                    $("#nombreRazonP1").val("");                    $("#apellidoP1").val("");                    $("#apellido2P1").val("");                    $("#telefonoP1").val("");                    $("#correoP1").val("");                    $("#mostrarDatosP1").hide();                }            </script>             ';    }    	public static function detalleEnc($m){	                 return '<div class="row">                                    <div class="col-lg-12">                                        <div class="panel panel-info">                                            <div class="panel-heading">                                            La encomienda se encuentra con el estado &nbsp;<strong>'.$m['estado'].'</strong>                                         </div>                                        <div class="panel-body" id="panelDetalleEncomienda">                                                                                        <div class="form-group input-group">                                               <span class="input-group-addon"><i class="fa fa-home"></i></span>                                               <input type="text" class="form-control" value="'.$m['dpto'].'"/>                                            </div>                                            <label>Habitan en el departamento</label>                                            <div class="table-responsive">                                                <table class="table table-condensed table-striped">                                                <tbody>                                                '.$m['habitan'].'                                                </tbody>                                                </table>                                                </div>                                            <div class="form-group input-group">                                                   <span class="input-group-addon">Remitente</span>                                                <input type="text" class="form-control" value="'.$m['remite'].'"/>                                            </div>                                            <div class="form-group input-group">                       