                     		foreach( $repeated as $i => $rItem )            		{            			if( $rItem['value'] === $value )            			{            				$inArray = true;            				++$repeated[$i]['count'];            			}            		}                         		if( false === $inArray )            		{            			$i = count($repeated);            			$repeated[$i] = array();            			$repeated[$i]['value'] = $value;            			$repeated[$i]['count'] = 1;            		}            	}                         	sort($repeated);                         	return $repeated;            }	}